    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
       if(txtCC.getText().trim().isEmpty() || txtCName.getText().trim().isEmpty()||
               jTextField3.getText().trim().isEmpty()|| txtCFee.getText().trim().isEmpty()){
           JOptionPane.showMessageDialog(this, "All fields are Mendatory, please check again");          
      }
      
      else
        
        
        try
        {
            MySqlConnection mysql = new MySqlConnection();
            Class.forName(mysql.MyDriver);
            Connection con = DriverManager.getConnection(mysql.Connection,"root","");
            
            int ccode = Integer.parseInt(txtCC.getText());
            String cname = txtCName.getText();           
            String cduration = jTextField3.getText();
            int cfee = Integer.parseInt(txtCFee.getText());
            int Lid = Integer.parseInt(jComboBox1.getSelectedItem().toString());
            String Lname = jTextField6.getText();
                                   
            
                              
            PreparedStatement ps = con.prepareStatement("INSERT INTO course VALUES ( ? , ? , ?, ?,?,?)");
            ps.setInt(1, ccode);
            ps.setString(2, cname);
            ps.setString(3, cduration);
            ps.setInt(4, cfee);
            ps.setInt(5,Lid);
            ps.setString(6, Lname);
            
            
            
            ps.execute();

            JOptionPane.showMessageDialog(this, "Course Details have been successfully recorded!");
            txtCC.setText("");
            txtCName.setText("");
            jTextField3.setText("");
            txtCFee.setText("");
            jTextField6.setText("");
            
           
          
        }        
        catch(Exception ex)
        {
            JOptionPane.showMessageDialog(this, ex.toString());
        }
    }//GEN-LAST:event_jButton1ActionPerformed

