    private void formWindowOpened(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowOpened
        
        
        try
        {
            MySqlConnection mysql = new MySqlConnection();
            Class.forName(mysql.MyDriver);
            Connection con = DriverManager.getConnection(mysql.Connection,"root","");
            Statement Stat = con.createStatement(); 
            String SelectQuery= "Select ID from lecturer_master";
            jComboBox1.removeAllItems();
            ResultSet rs= Stat.executeQuery(SelectQuery);
            while (rs.next())
            {
                
                jComboBox1.addItem(Integer.toString(rs.getInt("ID")));
            }
            
        }          
            
            catch(Exception ex)
             {
            System.out.println(ex);
        }   
           
    }//GEN-LAST:event_formWindowOpened

    private void jComboBox1PopupMenuWillBecomeInvisible(javax.swing.event.PopupMenuEvent evt) {//GEN-FIRST:event_jComboBox1PopupMenuWillBecomeInvisible
        String tmp = (String)jComboBox1.getSelectedItem();
        String Sql="Select* from lecturer_master where id = ?";
        try{
           MySqlConnection mysql = new MySqlConnection();
           Class.forName(mysql.MyDriver);
                   
           Connection con = DriverManager.getConnection(mysql.Connection,"root","");
            //Statement Stat = con.createStatement(); 
            PreparedStatement ps = con.prepareStatement(Sql);
            ps.setString(1, tmp);
            ResultSet rs=ps.executeQuery();
            if(rs.next())
            {
              String add=rs.getString("name");
              jTextField6.setText(add);
            }
            
}
catch (Exception ex)
{
    System.out.println(ex);
}
    }//GEN-LAST:event_jComboBox1PopupMenuWillBecomeInvisible

